import React, { useState, useRef, useEffect } from "react";
import { Typography, TextField } from "@mui/material";
import { FullScreenContainer, StyledButton } from "../common/loginsignupstyles";
import {
  StyledTypography,
  StyledPaper,
  StyledForm,
  StyledBottom,
  OuterInput,
  InnerInput,
  StyledBottomText,
  StyledLink,
  InnerTextBox,
  StyledPhoneDiv,
  StyledPhone,
  StyledSvg,
  StyledResendButton,
  BlueColor,
  GreyText,
  ErrorMessage
} from "./verifypage.styles";
import { useLocation, useNavigate } from 'react-router-dom';
import axios from "axios";
import image from "../../assets/SVG/plan-for-intense-storms.svg";


interface LocationState {
  PhoneNumber?: string;
}

export const Verify: React.FC = () => {
  const [otp, setOtp] = useState<string[]>(Array(6).fill(''));
  const [timer, setTimer] = useState(30);
  const [showResend, setShowResend] = useState(false);
  const [error, setError] = useState(false); // Add error state
  const otpRefs = useRef<(HTMLInputElement | null)[]>([]);
  const location = useLocation();
  const navigate = useNavigate();

  const locationState = location.state as LocationState;
  const phone = locationState?.PhoneNumber || '';

  useEffect(() => {
    if (otpRefs.current[0]) {
      otpRefs.current[0]?.focus();
    }
  }, []);

  useEffect(() => {
    if (timer > 0) {
      const countdown = setTimeout(() => setTimer(timer - 1), 1000);
      return () => clearTimeout(countdown);
    } else {
      setShowResend(true);
    }
  }, [timer]);

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>, index: number) => {
    const value = e.target.value;
    if (/^\d*$/.test(value) && value.length <= 1) {
      const newOtp = [...otp];
      newOtp[index] = value;
      setOtp(newOtp);

      // Focus the next input if a value is entered
      if (value && index < otpRefs.current.length - 1 && otpRefs.current[index + 1]) {
        otpRefs.current[index + 1]?.focus();
      }
    }
  };

  const handleKeyDown = (e: React.KeyboardEvent<HTMLInputElement>, index: number) => {
    if (e.key === 'Backspace' && !otp[index] && index > 0) {
      otpRefs.current[index - 1]?.focus();
    } else if (e.key === 'ArrowLeft' && index > 0) {
      otpRefs.current[index - 1]?.focus();
    } else if (e.key === 'ArrowRight' && index < otpRefs.current.length - 1) {
      otpRefs.current[index + 1]?.focus();
    }
  };

  const handleSubmit = async () => {
    const otpString = otp.join('');
    try {
      const response = await axios.post('http://localhost:8000/api/v1/users/verify-otp', {
        otp: otpString,
        PhoneNumber: phone
      }, { withCredentials: true });
      if (response.status === 200) {
        navigate('/');
      } else {
        setError(true); // Set error state if verification fails
      }
    } catch (error) {
      console.error("There was an error!", error);
      setError(true); // Set error state if there's an error
    }
  };

  const handleResend = async () => {
    try {
      // Logic to resend the OTP
      await axios.post('http://localhost:8000/api/v1/users/resend-otp', { PhoneNumber: phone });
      setTimer(30); // Reset the timer
      setShowResend(false); // Hide resend link
    } catch (error) {
      console.error("Error resending OTP:", error);
      alert("An error occurred while resending OTP. Please try again.");
    }
  };

  return (
    <FullScreenContainer>
      <StyledPaper>
        <StyledForm>
          <StyledTypography>
            Verify your Phone Number
          </StyledTypography>
          <StyledBottom>
            Enter the OTP sent to 
          </StyledBottom>
          <StyledPhoneDiv>
            <StyledPhone>
              {phone}
            </StyledPhone>
            <StyledSvg src={image} onClick={() => navigate('/signin')} />
          </StyledPhoneDiv>
          <OuterInput>
            <InnerInput>
              {otp.map((data, index) => (
                <TextField
                  key={index}
                  type="text"
                  value={data}
                  onChange={(e) => handleChange(e as React.ChangeEvent<HTMLInputElement>, index)}
                  onKeyDown={(e) => handleKeyDown(e, index)}
                  inputProps={{ maxLength: 1 }} // Use inputProps to set maxLength
                  inputRef={(el) => (otpRefs.current[index] = el)} // Use inputRef to set the ref
                  error={error} // Apply error styling
                  helperText={error ? "Incorrect OTP" : ""}
                />
              ))}
            </InnerInput>
          </OuterInput>
          {error && <ErrorMessage>OTP verification failed. Please try again.</ErrorMessage>}
          <StyledBottomText>
            {showResend ? (
              <>
                <GreyText>
                  Didn't receive the code?
                </GreyText>
                <BlueColor>
                  <StyledResendButton onClick={handleResend}>Resend</StyledResendButton>
                </BlueColor>
              </>
            ) : (
              <>
                <GreyText>
                  Expect OTP in 
                </GreyText>
                <BlueColor>
                  {timer} seconds
                </BlueColor>
              </>
            )}
          </StyledBottomText>
          <StyledButton onClick={handleSubmit}>
            Continue
          </StyledButton>
        </StyledForm>
      </StyledPaper>
    </FullScreenContainer>
  );
};
