import { Typography } from "@mui/material";
import { 
  FullScreenContainer, 
  StyledText, 
  StyledTextField, 
  StyledBottomText, 
  StyledLink, 
  StyledButton 
} from "../common/loginsignupstyles";
import { 
  StyledTypography, 
  StyledPaper, 
  StyledForm, 
  StyledBottom, 
  OuterInput, 
  InnerInput, 
  InnerTextBox 
} from "./verifypage.styles";
import { useLocation, useNavigate } from 'react-router-dom';
import { useState, useRef } from "react";
import axios from "axios";
interface LocationState {
    emailId?: string;
  }
  
  export const Verify: React.FC = () => {
    const [otp, setOtp] = useState<string[]>(Array(6).fill(''));
    const otpRefs = useRef<HTMLInputElement[]>(Array(6).fill(null));
    const location = useLocation<LocationState>();
    const navigate = useNavigate();
    const email = location.state?.emailId || '';
  
    const handleChange = (e: React.ChangeEvent<HTMLInputElement>, index: number) => {
      const value = e.target.value;
      if (/^\d*$/.test(value) && value.length <= 1) {
        const newOtp = [...otp];
        newOtp[index] = value;
        setOtp(newOtp);
  
        // Focus the next input
        if (value && index < otpRefs.current.length - 1 && otpRefs.current[index + 1]) {
          otpRefs.current[index + 1].focus();
        }
      }
    };
  
    const handleSubmit = async () => {
      const otpString = otp.join('');
      try {
        const response = await axios.post('http://localhost:8000/api/v1/users/verify-otp', {
          otp: otpString,
          emailId: email
        }, { withCredentials: true });
        if (response.status === 200) {
          alert("OTP verification successful!");
          navigate('/');
        } else {
          alert(response.data.message || "OTP verification failed");
        }
      } catch (error) {
        console.error("There was an error!", error);
        alert("An error occurred while verifying OTP. Please try again.");
      }
    };
  
    return (
      <FullScreenContainer>
        <StyledPaper>
          <StyledForm>
            <StyledTypography>
              Verify your Phone Number
            </StyledTypography>
            <StyledBottom>
              Enter the OTP
            </StyledBottom>
            <OuterInput>
              <InnerInput>
                {otp.map((data, index) => (
                  <InnerTextBox
                    key={index}
                    type="text"
                    maxLength={1}
                    value={data}
                    onChange={(e) => handleChange(e, index)}
                    ref={(el) => otpRefs.current[index] = el as HTMLInputElement}
                  />
                ))}
              </InnerInput>
            </OuterInput>
            <StyledBottomText>
              Didn't receive the code? 
              <StyledLink to='/verify'>Resend</StyledLink>
            </StyledBottomText>
            <StyledButton onClick={handleSubmit}>
              Continue
            </StyledButton>
          </StyledForm>
        </StyledPaper>
      </FullScreenContainer>
    );
  };
