import React from 'react';

interface CardProps {
  imageBuyer: string;
  buyer: string;
  seller: string;
  imageSeller: string;
  unitcost: string;
  xirr: string;
  totalunit:string;
  remainingunit:string;
  tenure: string;
}

export const CardComponent: React.FC<CardProps> = ({
  imageBuyer,
  buyer,
  seller,
  imageSeller,
  unitcost,
  xirr,
  totalunit,
  remainingunit,
  tenure
}) => {
  return (
    <div className="w-80 h-60 shadow rounded-2xl bg-white px-4 py-6">
      <div className="flex justify-between">
        <div className="flex items-center">
          <img className="h-12 w-12 object-cover object-center" src={imageBuyer} alt="Buyer" />
          <div className="ml-2">
            <p className="text-gray-400 text-xs font-medium">Buyer</p>
            <p className="font-medium text-sm">{buyer}</p>
          </div>
        </div>
        <div className="flex items-center">
          <div className="mr-2 text-right">
            <p className="text-gray-400 text-xs font-medium">Seller</p>
            <p className="font-medium text-sm">{seller}</p>
          </div>
          <img className="h-12 w-12 object-cover object-center" src={imageSeller} alt="Seller" />
        </div>
      </div>
     
      <div className="grid grid-cols-2 divide-x divide-gray-200 px-1 py-5">
         <div>
            <p className="text-gray-400 text-xs font-medium">Unit cost</p>
            <p className="font-medium text-sm">{unitcost}</p>
          </div>
          <div className='text-right'>
            <p className='text-gray-400 text-xs font-medium'>XIRR</p>
            <p className='font-medium text-sm'>{xirr}</p>
         
          </div>
  
      </div>
      <div>
        <div>
        <p className='text-gray-400 text-xs font-medium '>Unit left</p>
        <p  className='font-medium text-sm'>{remainingunit}/{totalunit}</p>
        </div>
        
      </div>
    
    </div>
  );
};
