import { Typography ,TextField, Button} from '@mui/material';
import React, { useState } from 'react';
import {FullScreenContainer ,StyledPaper ,StyledTypography ,StyledForm,StyledInput,StyledText
  ,StyledTextField,StyledBottomText,StyledLink,StyledButton} from "../common/loginsignupstyles"
import { useNavigate } from 'react-router-dom';

export const Login = () => {
  const nav=useNavigate();
  const[number ,setNumber]=useState('');

  function onClick(){
  
     nav('/verify { state: { number } }')
  
  }
  
  return (
    <FullScreenContainer>
      <StyledPaper>
        <StyledTypography>
          Log in
        </StyledTypography>
       <StyledForm>
          <StyledInput>
            <StyledText>
              Email or Mobile Number
            </StyledText>
            <StyledTextField
               variant='outlined'
               placeholder='Email or Mobile Number'
               onChange={(e)=>{
                  setNumber(e.target.value);
               }}
            />
        
            </StyledInput>
            <StyledBottomText>
         By clicking Continue, you agree to our
         <StyledLink to="/terms">Terms & Conditions </StyledLink> and {' '}
         <StyledLink to="/terms"> Privacy Policy</StyledLink>.
   
         </StyledBottomText>
         <StyledButton  onClick={onClick} >
          Continue
         </StyledButton>
          
       </StyledForm>

       <StyledBottomText>
        Don't have an Account?
        <StyledLink to='/signup'>Sign up instead</StyledLink>
       </StyledBottomText>
      </StyledPaper>
    </FullScreenContainer>
  );
};
