interface InputProps{
label :String,
placeholder:string
}
export const Input : React.FC<InputProps>=({label ,placeholder})=>{
    return <div className="m-15">
    <div className="text-sm font-medium  text-left pb-1">
       {label}
    </div>
    <input  placeholder={placeholder} className=" w-80 border rounded-md px-2 py-2 "></input>
</div>
}
