import React, { useState, useEffect } from 'react';
import { Link, useParams } from 'react-router-dom';
import {
  Grid, Typography, Card, CardContent, CardHeader, CircularProgress,
  Alert, Button, Box, LinearProgress, Container
} from '@mui/material';
import { styled, ThemeProvider, createTheme } from '@mui/material/styles';
import { FullScreenContainer} from "../common/loginsignupstyles"

import axios from 'axios';
import OrderDetailsPopup from '../OrderDetails/OrderDetailsModel'; 

interface Product {
  _id: string;
  BuyerName: string;
  SellerName: string;
  UnitPrice: number;
  TotalUnits: number;
  Tenure: number;
  RemainingUnits:number;
  ImageSeller:string;
  ImageBuyer:string;
  Xirr: number;
  ID: string;
}
const StyledCard = styled(Card)(({ theme }) => ({
  transition: 'transform 0.3s, box-shadow 0.3s',
  '&:hover': {
    transform: 'scale(1.05)',
    boxShadow: '0 6px 12px rgba(0, 0, 0, 0.2)',
  },
  borderRadius: 15,
  padding: theme.spacing(2),
  margin: theme.spacing(2),
}));

const StyledPaper = styled('div')`
  display: flex;
  flex-direction: column;
  align-items: center;
  
  max-width: 450px;
  width: 28.5rem;
  background-color:red ;
  border-radius: 1.5rem;
  boxShadow: 3 ;
`;
const StyledCardHeader = styled(CardHeader)(({ theme }) => ({
  width:'100%',
  backgroundColor: 'gray',
  textAlign: 'center',
  color: 'white',
  '& .MuiCardHeader-subheader': {
    color: 'white',
  },
  padding: theme.spacing(2),
  borderTopLeftRadius: 15,
  borderTopRightRadius: 15,
}));

const StyledCardContent = styled(CardContent)(({ theme }) => ({
  width:'100%',
  padding: theme.spacing(3),
  textAlign: 'center',
  backgroundColor: '#f0f0f0',

}));

const IncrementDecrementBox = styled(Box)(({ theme }) => ({
  display: 'flex',
  alignItems: 'center',
  justifyContent: 'center',
  marginTop: theme.spacing(2),
}));

const IncrementDecrementButton = styled(Button)(({ theme }) => ({
  margin: theme.spacing(0, 1),
  backgroundColor: 'gray',
  color:'white',
}));

const StyledButton =styled('button')`
    display: inline-flex;
    align-items: center;
    justify-content: center;
    position: relative;
    box-sizing: border-box;
    -webkit-tap-highlight-color: transparent;
    outline: 0px;
    border: 0px;
    margin: 12px 0px 0px;
    cursor: pointer;
    user-select: none;
    vertical-align: middle;
    appearance: none;
    text-decoration: none;
    line-height: 1;
    font-family: Inter, sans-serif;
    min-width: 64px;
    padding: 8px 22px;
    border-radius: 8px;
    width: 100%;
    
    text-align: center;
    font-size: 1.125rem;
    height: 2.4rem;
    color: rgb(176, 135, 243);
    background-color: rgb(246, 237, 255) ;
    font-weight: 600;
    transition: all 500ms cubic-bezier(0.4, 0, 0.2, 1) 0ms;
    &:hover{
       background-color: rgb(176, 135, 243);
       color:rgb(246, 237, 255) ;
      
    }

`
const ProductCard: React.FC<{ product: Product }> = ({ product }) => {
  const [units, setUnits] = useState(0);
  const [orderPopupOpen, setOrderPopupOpen] = useState(false);
  const [orderDetails, setOrderDetails] = useState<Product | null>(null);
  const incrementUnits = () => {
    if (units < product.TotalUnits) {
      setUnits(units + 1);
    }
  };

  const decrementUnits = () => {
    if (units > 0) {
      setUnits(units - 1);
    }
  };

  const handleBuyNow = async () => {
    try {
     
      const requestData = {
        ProductId: product.ID,
        NoOfUnits: units,
      };
      console.log('Request Data:', requestData);
      const response = await axios.post(
        'http://localhost:8000/api/v1/order/details', requestData, { withCredentials: true }
      );
      console.log("after link")
      if (response.data && response.data.data) {
        console.log(response.data.data)
        setOrderDetails(response.data.data);
        setOrderPopupOpen(true);
      } else {
        throw new Error('Order details not found');
      }
    } catch (error) {
      console.error('Error fetching order details:', error);
    }
  };

  const handleClosePopup = () => {
    setOrderPopupOpen(false);
  };

  const progress = (units / product.TotalUnits) * 100;

  return (
  
      <StyledCard>
      <StyledPaper>
      <StyledCardHeader
        title={`Buyer: ${product.BuyerName}`}
        subheader={`Seller: ${product.SellerName}`}
      />
      <StyledCardContent>
        <Typography variant="body1">Unit Price: ₹{product.UnitPrice}</Typography>
        <Typography variant="body1">Tenure: {product.Tenure} Days</Typography>
        <Typography variant="body1">Xirr: {product.Xirr}%</Typography>
        <Typography variant="body1">Total Units Left: {product.RemainingUnits - units} / {product.TotalUnits}</Typography>

        <LinearProgress variant="determinate" value={progress} sx = {{height: 10, borderRadius: 30, m:2} } />

        <IncrementDecrementBox>
          <IncrementDecrementButton variant="contained" onClick={decrementUnits} disabled={units === 0}>
            -
          </IncrementDecrementButton>
          <Typography variant="body1" display="inline" mx={2}>
            {units}
          </Typography>
          <IncrementDecrementButton variant="contained"  onClick={incrementUnits} disabled={units >= product.TotalUnits}>
            +
          </IncrementDecrementButton>
        </IncrementDecrementBox>

        <Button onClick={handleBuyNow} sx={{ textDecoration: 'none', '&:hover': { cursor: 'pointer', color: 'blue', backgroundColor: 'white' }, backgroundColor: '#c0e5f2', m:3 }}>
          Buy Now
        </Button>
        <StyledButton>Buy Now</StyledButton>

        <OrderDetailsPopup open={orderPopupOpen} onClose={handleClosePopup} product={orderDetails} units={units} />
    
      </StyledCardContent>
        
      </StyledPaper>
      </StyledCard>

     
  );
};

export const ProductDetails = () => {
  const params = useParams<{ productid: string }>();
  const [loading, setLoading] = useState(true);
  const [product, setProduct] = useState<Product | null>(null);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    const fetchProduct = async () => {
      try {
        console.log(params.productid)
        console.log(params)
        const response = await axios.get(`http://localhost:8000/api/v1/product/${params.productid}`, { withCredentials: true });
        if (response.data && response.data.data) {
          setProduct(response.data.data);
          console.log(product);
        } else {
          throw new Error('Product data not found');
        }
        setLoading(false);
      } catch (error) {
        console.error('Error fetching product details:', error);
        setError('Error fetching product details');
        setLoading(false);
      }
    };

    fetchProduct();
  }, [params.productid]);

  if (loading) {
    return (
      <Container>
        <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', minHeight: '70vh' }}>
          <CircularProgress />
        </Box>
      </Container>
    );
  }

  if (error) {
    return (
      <Container>
        <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', minHeight: '70vh' }}>
          <Alert severity="error">{error}</Alert>
        </Box>
      </Container>
    );
  }

  return product ? (
    <Container>
      <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', minHeight: '70vh' }}>
        <ProductCard product={product} />
      </Box>
    </Container>
  ) : (
    <Container>
      <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', minHeight: '70vh' }}>
        <Typography variant="h6">Product not found</Typography>
      </Box>
    </Container>
  );
};
