import React from 'react';
import { Dialog, DialogTitle, DialogContent, DialogActions, Button, Typography } from '@mui/material';
 // Adjust the import path based on your project structure
 interface Product {
  _id: string;
  BuyerName: string;
  SellerName: string;
  UnitPrice: number;
  TotalUnits: number;
  Tenure: number;
  RemainingUnits:number;
  ImageSeller:string;
  ImageBuyer:string;
  Xirr: number;
  ID: string;
}

interface OrderDetailsPopupProps {
  open: boolean;
  onClose: () => void;
  product: Product | null;
  units: number;
}

const OrderDetailsPopup: React.FC<OrderDetailsPopupProps> = ({ open, onClose, product, units }) => {
  const navigate = useNavigate();

  const handleClose = () => {
    onClose();
    navigate('/dashboard');
  };

  if (!product) return null;

  return (
    <Dialog open={open} onClose={onClose}>
      <DialogTitle>Order Details</DialogTitle>
      <DialogContent>
        <Typography>Buyer: {product.BuyerName}</Typography>
        <Typography>Seller: {product.SellerName}</Typography>
        <Typography>Units: {units}</Typography>
        <Typography>Unit Price: ₹{product.UnitPrice}</Typography>
        <Typography>Total Price: ₹{product.UnitPrice * units}</Typography>
      </DialogContent>
      <DialogActions>
        <Button onClick={handleClose} color="primary">
          Close
        </Button>
      </DialogActions>
    </Dialog>
  );
};

export default withAuth(OrderDetailsPopup);
