import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { CircularProgress, Container, Box, Typography, Button, Alert } from '@mui/material';
import axios from 'axios';
import { styled } from '@mui/material/styles';

interface Product {
  _id: string;
  BuyerName: string;
  SellerName: string;
  UnitPrice: number;
  TotalUnits: number;
  Tenure: number;
  RemainingUnits: number;
  Xirr: number;
  Repayment: string;
  ImageSeller: string;
  ImageBuyer: string;
}

const StyledImage = styled('img')({
  maxWidth: '100%',
  borderRadius: '8px',
});

export const ProductDetails = () => {
  const { productId } = useParams<{ productId: string }>();
  const [product, setProduct] = useState<Product | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    const fetchProduct = async () => {
      try {
        const response = await axios.get(`http://localhost:8000/api/v1/product/${productId}`, { withCredentials: true });
        if (response.data && response.data.data) {
          setProduct(response.data.data);
        } else {
          setError('Product not found');
        }
      } catch (err) {
        console.error(err);
        setError('Error fetching product details');
      } finally {
        setLoading(false);
      }
    };

    fetchProduct();
  }, [productId]);

  if (loading) {
    return (
      <Container>
        <Box display="flex" justifyContent="center" alignItems="center" minHeight="70vh">
          <CircularProgress />
        </Box>
      </Container>
    );
  }

  if (error) {
    return (
      <Container>
        <Box display="flex" justifyContent="center" alignItems="center" minHeight="70vh">
          <Alert severity="error">{error}</Alert>
        </Box>
      </Container>
    );
  }

  return product ? (
    <Container>
      <Box display="flex" justifyContent="center" alignItems="center" flexDirection="column" minHeight="70vh">
        <StyledImage src={product.ImageBuyer} alt={product.BuyerName} />
        <Typography variant="h4">Buyer: {product.BuyerName}</Typography>
        <StyledImage src={product.ImageSeller} alt={product.SellerName} />
        <Typography variant="h4">Seller: {product.SellerName}</Typography>
        <Typography variant="body1">Unit Price: ₹{product.UnitPrice}</Typography>
        <Typography variant="body1">Total Units: {product.TotalUnits}</Typography>
        <Typography variant="body1">Remaining Units: {product.RemainingUnits}</Typography>
        <Typography variant="body1">Tenure: {product.Tenure} Days</Typography>
        <Typography variant="body1">Xirr: {product.Xirr}%</Typography>
        <Typography variant="body1">Repayment: ₹{product.Repayment}</Typography>
        <Button variant="contained" color="primary" onClick={() => alert('Purchase functionality not implemented yet')}>
          Purchase
        </Button>
      </Box>
    </Container>
  ) : null;
};

export default ProductDetails;
