import { Typography } from "@mui/material"
import {FullScreenContainer ,StyledText
    ,StyledTextField,StyledBottomText,StyledLink,StyledButton} from "../common/loginsignupstyles"
  import { StyledTypography,StyledPaper ,StyledForm, StyledBottom,OuterInput, InnerInput, InnerTextBox } from "./verifypage.styles"
  import { useLocation, useNavigate } from 'react-router-dom';
  import { useState } from "react";
  import axios from "axios";

export const Verify=()=>{
    const location = useLocation();
    const  nav =useNavigate();
    function onClick(){
        nav('/')
        const [otp, setOtp] = useState(Array(6).fill(''));
        const location = useLocation();
        const navigate = useNavigate();
        const { state } = location;
        const email = state?.email || '';
      
        const handleChange = (element, index) => {
          const value = element.value;
          if (/^\d*$/.test(value)) {
            let newOtp = [...otp];
            newOtp[index] = value;
            setOtp(newOtp);
            if (value && index < 5) {
              document.getElementById(`otp-input-${index + 1}`).focus();
            }
          }
        };
      
        const handleSubmit = async () => {
          const otpString = otp.join('');
          try {
            const response = await axios.post('http://localhost:8080/api/verify-otp', {
              otp: otpString,
              email_id: email
            });
            if (response.data.success) {
              navigate('/dashboard');
            } else {
              alert(response.data.message);
            }
          } catch (error) {
            console.error("There was an error!", error);
            alert("An error occurred while verifying OTP. Please try again.");
          }
        };
    }
    return  <FullScreenContainer>
        <StyledPaper>
            <StyledForm>
                <StyledTypography>
                    Verify your Phone Number
                </StyledTypography>
                <StyledBottom>
                Enter the OTP 
                </StyledBottom>
                
                {/* <p>{` ${number}`}</p> */}
                <OuterInput>
                    <InnerInput>
                    {otp.map((data, index) => (
                <InnerTextBox 
                  key={index} 
                  id={`otp-input-${index}`} 
                  value={data} 
                  onChange={(e) => handleChange(e.target, index)} 
                  maxLength="1"
                />
              ))}
                     </InnerInput>


                </OuterInput>
                
       <StyledBottomText >
        Don't recieve the code ? 
        <StyledLink to='/verify'>Resend</StyledLink>
       </StyledBottomText>
       <StyledButton  onClick={onClick} >
          Continue
         </StyledButton>

            </StyledForm>
        </StyledPaper>
    </FullScreenContainer>
}
