import { FullScreenContainer } from "../common/loginsignupstyles";
import { AppDiv, CenterAppbar, StyledImage, StyledLink } from "../common/Dashboardmain.styles";
import { CardInnerDiv, CardOuterDiv } from "./Dashboard.styles";
import photo from "../../assets/logos/Logo_with_text.svg";
import CardComponent from './DashboardComponents/CardComponent';
import { useState, useEffect } from "react";
import { Grid, Alert, CircularProgress } from "@mui/material";
import axios from "axios";
import withAuth from "../AuthChecker";
import { Navigate, useNavigate } from "react-router-dom";

interface Product {
  _id: string;
  BuyerName: string;
  SellerName: string;
  UnitPrice: number;
  TotalUnits: number;
  Tenure: number;
  RemainingUnits: number;
  Xirr: number;
  Repayment: string;
  ImageSeller: string;
  ImageBuyer: string;
}

export const Dashboard = () => {
  const [products, setProducts] = useState<Product[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const nav=useNavigate();

  const fetchProducts = async () => {
    try {
      const response = await axios.get('http://localhost:8000/api/v1/products', { withCredentials: true });
      console.log(response.data.products);
      if (response.data && response.data.data) {
        setProducts(response.data.data);
      } else {
        setError('No products found');
      }
    } catch (err) {
      console.error(err);
      nav('/login')
      setError('Error fetching products');

    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchProducts();
  }, []);

  if (loading) {
    return (
      <Grid container justifyContent="center" alignItems="center" style={{ height: '100vh' }}>
        <CircularProgress />
      </Grid>
    );
  }

  if (error) {
    return (
      <Grid container justifyContent="center" alignItems="center" style={{ height: '100vh' }}>
        <Alert severity="error">{error}</Alert>
      </Grid>
    );
  }

  return (
    <FullScreenContainer>
      <AppDiv>
        <StyledImage src={photo}></StyledImage>
        <CenterAppbar></CenterAppbar>
        <div>
          <StyledLink to="/"> Logout</StyledLink>
        </div>
      </AppDiv>

      <CardOuterDiv>
        {products.map((product) => (
          <CardComponent
            key={product._id}
            _id={product._id}
            BuyerName={product.BuyerName}
            SellerName={product.SellerName}
            UnitPrice={product.UnitPrice}
            Repayment={product.Repayment}
            Xirr={product.Xirr}
            ImageBuyer={product.ImageBuyer}
            ImageSeller={product.ImageSeller}
            TotalUnits={product.TotalUnits}
            RemainingUnits={product.RemainingUnits}
            Tenure={product.Tenure}
          />
        ))}
      </CardOuterDiv>
    </FullScreenContainer>
  );
};
export default withAuth(Dashboard);
