import {CardInnerDiv,CardOuterDiv} from "./Dashboard.styles"
import tataimage from "../../assets/pngs/Final logo - Tata motors .png"
import photo from "../../assets/logos/Logo_with_text.svg"
import berrysimage from "../../assets/pngs/Berrys.png";
import proto from "../../assets/pngs/Proto.png";
import CardComponent from './DashboardComponents/CardComponent';
import { useState,useEffect } from "react"
import { Grid,Alert,CircularProgress } from "@mui/material"
import axios from "axios"

