//     buyer: "TATA Motors",
//     seller :"Berrys Auto",
//     imageSeller:berrysimage,
//     unitcost:"10,000",
//     Repayment:"52,883",
//     xirr:"15.29%",
//     totalunit:"1000",
//     remainingunit:"234",
//     tenure:"60"
// }]
interface Product {
  _id: number;
  BuyerName: string;
  SellerName: string;
  UnitPrice:number;
  TotalUnits: number;
  Tenure: number;
 RemainingUnits:number;
  Xirr: number;
  Repayment:string;
  ImageSeller:string;
  ImageBuyer:string;
}
const Dashboard = () => {
  const [products, setProducts] = useState<Product[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  const fetchProducts = async () => {
    try {
      const response = await axios.get('http://localhost:8000/api/v1/products', { withCredentials: true });
      console.log(response.data.data);
      if (response.data && response.data.products) {
        setProducts(response.data.products);
      } else {
        setError('No products found');
      }
    } catch (err) {
      console.error(err);
      setError('Error fetching products');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchProducts();
  }, []);

  if (loading) {
    return (
      <Grid container justifyContent="center" alignItems="center" style={{ height: '100vh' }}>
        <CircularProgress />
      </Grid>
    );
  }

  if (error) {
    return (
      <Grid container justifyContent="center" alignItems="center" style={{ height: '100vh' }}>
        <Alert severity="error">{error}</Alert>
      </Grid>
    );
  }

  return (
    <FullScreenContainer>
      <AppDiv>
        <StyledImage src={photo}></StyledImage>
        <CenterAppbar></CenterAppbar>
        <div>
          <StyledLink to="/signup"> Logout</StyledLink>
        </div>
      </AppDiv>

      <CardOuterDiv>
        {products.map((product) => (
          <CardComponent
            key={product._id}
            _id={product._id}
            BuyerName={product.BuyerName}
            SellerName={product.SellerName}
            UnitPrice={product.UnitPrice}
            Repayment={product.Repayment}
            Xirr={product.Xirr}
            ImageBuyer={product.ImageBuyer}
            ImageSeller={product.ImageSeller}
            TotalUnits={product.TotalUnits}
            RemainingUnits={product.RemainingUnits}
            Tenure={product.Tenure}
          />
        ))}
      </CardOuterDiv>
    </FullScreenContainer>
  );
};

export default Dashboard;
