import { Typography } from "@mui/material";
import { 
  FullScreenContainer,
  StyledPaper,
  StyledForm,
  StyledInput,
  StyledText,
  StyledTextField,
  StyledBottomText,
  StyledLink,
  StyledButton,
  SignupDoublegrid, Signupleft,
  LeftImage,
  LeftText,
  SignupRight,
  StyledReffText,
  StyledCheck
} from "../common/loginsignupstyles";
import { ReactComponent as Logo } from '../images/Logo_with_text.svg';
import { useNavigate } from "react-router-dom";
import { useState } from "react";
import axios from "axios";
import {getCookie} from "../../utils/Cookies"


export const Signup = () => {
  const [phoneNo, setPhoneno] = useState('');
  const [email, setEmail] = useState('');
  const nav = useNavigate();

  const handleRegister = async () => {
    const user = {
      emailId: email,
      phoneNumber: phoneNo,
    };

    try {
      const response = await axios.post("http://localhost:8000/api/v1/users/signup", user);
      if (response.status === 201 || response.status === 200) {
      
        nav("/verify", { state: { emailId: email } });
      } else {
        alert("Registration failed: " + (response.data.message || "Unknown error"));
      }
    } catch (error) {
      if (axios.isAxiosError(error)) {
        if (error.response) {
          alert("Error: " + (error.response.data.message || "Unknown error from server"));
        } else if (error.request) {
          alert("Error: No response from server. Please try again later.");
        } else {
          alert("Error: " + error.message);
        }
      }
    }
  };

  return (
    <FullScreenContainer>
      <StyledPaper>
        <Typography sx={{ textAlign: "center", fontSize: "1.75rem", fontWeight: "800", fontFamily: "Baikal, Inter, sans-serif", letterSpacing: "-0.025em" }}>
          Get Started
        </Typography>
        <Typography sx={{ textAlign: "center", fontSize: "12px",  paddingTop:"0.3rem"}}>
          Please enter your details to create your account
        </Typography>
        <StyledForm>
          <StyledInput>
            <StyledText>Phone No.</StyledText>
            <SignupDoublegrid>
              <Signupleft>
                <LeftImage src="https://tyke-assets.s3.amazonaws.com/flags/in-flag.jpg" />
                <LeftText>+91</LeftText>
              </Signupleft>
              <SignupRight>
                <StyledTextField
                  onChange={(e) => { setPhoneno(e.target.value); }}
                  variant='outlined'
                  placeholder='Phone No.'
                />
              </SignupRight>
            </SignupDoublegrid>
          </StyledInput>
          <StyledCheck>

          </StyledCheck>
          <StyledInput>
            <StyledReffText>Referral Code (optional)</StyledReffText>
            <StyledTextField
              onChange={(e) => { setEmail(e.target.value); }}
              variant='outlined'
              placeholder='ENTER YOUR FRIEND REFFERAL CODE'
            />
            <StyledBottomText>
              By clicking Continue, you agree to our
              <StyledLink to="/terms"> Terms & Conditions </StyledLink>
              and {' '}
              <StyledLink to="/terms"> Privacy Policy</StyledLink>.
            </StyledBottomText>
            <StyledButton onClick={handleRegister}>Get OTP</StyledButton>
          </StyledInput>
        </StyledForm>
        <StyledBottomText>
          Already have an Account?
          <StyledLink to='/signin'>Log in instead </StyledLink>
        </StyledBottomText>
      </StyledPaper>
    </FullScreenContainer>
  );
};
