import { TextField } from '@mui/material';
import styled from 'styled-components';
import { Link } from 'react-router-dom';
export const  StyledTypography=styled('div')`
margin: 1.25rem 0px 0.5rem;
    font-weight: 700;
    line-height: 1.2;
    font-size: 1.25rem;
    font-family: Baikal, Inter, sans-serif;
    letter-spacing: -0.025em;
    text-align: center;
`
export const StyledPaper=styled('div')`
    width: 32.25rem;
    background: rgb(255, 255, 255);
    border-radius: 1.5rem;
    padding: 2.5rem 4rem;

`
export  const StyledForm=styled('div')`

    margin: 0px;
    color: rgb(12, 12, 12);
    line-height: 1.4;
    font-size: 1.175rem;
    letter-spacing: -0.01em;
    font-family: Inter, sans-serif;
    font-weight: 400;
    background-color: rgb(255, 255, 255);
}
 
  
`
export const StyledBottom=styled('div')`
     margin: 0px;
    font-size: 0.875rem;
    line-height: 1.4;
    color: rgb(107, 107, 107);
    font-weight: 500;
    display: flex;
    -webkit-box-pack: center;
    justify-content: center;
    -webkit-box-align: center;
    align-items: center;
    flex-direction: column;
}
`
export const OuterInput=styled('div')`
    margin: 20px;
    color: rgb(12, 12, 12);
    line-height: 1.4;
    font-size: 1.125rem;
    letter-spacing: -0.01em;
    font-family: Inter, sans-serif;
    font-weight: 400;
    background-color: rgb(255, 255, 255)
    
}
`
export const InnerInput=styled('div')`
    height: 3rem;
    font-size: 1.125rem;
    font-weight: 600;
    display: flex;
    gap: 1.25rem;
    
}
`
export const InnerTextBox=styled.input`
    font: inherit;
    text-align: center;
    width: 2.8em;
    border-radius: 10px;
    border-style: solid;
    border-color: rgb(206, 206, 206);
    border-width: 1px;
    caret-color: rgb(107, 107, 107);
    outline: none;
}
`
export const StyledBottomText=styled('div')`
    display:flex;
    flex-wrap: wrap ;
    gap:0.2rem;
    justify-content:center;
    font-size: 0.75rem;
    line-height: 1.4;
    font-family: Baikal, Inter, sans-serif;
    margin: 1rem 0px 0.25rem;
    font-weight: 500;
    

}`

export const StyledLink = styled(Link)`
  color: #007aff;
  font-weight: bold;
  text-decoration: none; 
  &:hover {
   
    cursor: pointer;
  }
`;

export const StyledPhoneDiv=styled.div`
    display: flex;
    Justify-content: center;
}

`
export const StyledPhone=styled.div`
margin: 0px;
    font-size: 0.875rem;
    line-height: 1.4;
    color: rgb(12, 12, 12);
    font-weight: 600;
    letter-spacing: 0.01em;
}
`
export const StyledSvg=styled.img`
    cursor: pointer;
    width:1.5rem;
    height:1.5rem;


`
