// CardComponent.tsx
import React from 'react';
import { CardComponentStyled, CardUpper ,CardUppermore, FullDiv, Leftpart, LeftTextpart, Styledimage, Upperpart, UpperText ,Outerleftpartdiv,
   LeftBottomText, Rightpart,Imagediv, RightTextpart, RightBottomText, 
   CardMiddle,LeftMost,
   CardMiddleLeft,
   MarginBetween,
   Rightmost,
   Styledrepayment,
   CardMiddleRight} from "../Dashboard.styles";

interface CardProps {
  id: number;
  imageBuyer: string;
  buyer: string;
  seller: string;
  imageSeller: string;
  tenure: string;
  unitcost: string;
  repayment:string;
  totalunit: string;
  xirr: string;
  remainingunit: string;
}

const CardComponent: React.FC<CardProps> = ({ id, imageBuyer, buyer, seller, imageSeller,repayment, tenure, unitcost, totalunit, xirr, remainingunit }) => {
  return (
    <CardComponentStyled>
      <CardUpper>
        <CardUppermore>
          <UpperText>
           INVOICE DISCOUNTING
          </UpperText>
          <FullDiv>
            <Upperpart>
              <Leftpart>
                <Styledimage src={imageBuyer}/>
                <Outerleftpartdiv>
                <LeftTextpart>Buyer</LeftTextpart>
                <LeftBottomText>{buyer}</LeftBottomText>
                </Outerleftpartdiv>
                
              </Leftpart>
              <Rightpart>
              <Imagediv>
                <Styledimage src={imageSeller}/>
                </Imagediv>
              <Outerleftpartdiv>
               
               
                <RightTextpart>Seller</RightTextpart>
                <RightBottomText>{seller}</RightBottomText>
                
               </Outerleftpartdiv>
              </Rightpart>

            </Upperpart>
            <CardMiddle>
               <CardMiddleLeft>
                <LeftMost>
                 <LeftTextpart>Unit Cost</LeftTextpart>
                 <LeftBottomText>₹ {unitcost}</LeftBottomText>

                </LeftMost>
                <MarginBetween></MarginBetween>
                <Rightmost>
                <LeftTextpart>Repayment/Unit</LeftTextpart>
                <Styledrepayment>₹ {repayment}</Styledrepayment>

                </Rightmost>
            
               </CardMiddleLeft>
               <CardMiddleRight>
               <LeftTextpart>Tenure</LeftTextpart>
               <LeftBottomText>{tenure}</LeftBottomText>
               </CardMiddleRight>


      </CardMiddle>
          </FullDiv>
          
          </CardUppermore>  
          
      

      </CardUpper>

    </CardComponentStyled>
  );
};

export default CardComponent;
